"use client"

import * as React from "react"
import * as ToastPrimitives from "@radix-ui/react-toast"
import { cva, type VariantProps } from "class-variance-authority"
import { X } from "lucide-react"

import { cn } from "@/lib/utils"

const ToastProvider = ToastPrimitives.Provider

const ToastViewport = React.forwardRef<
  React.ElementRef<typeof ToastPrimitives.Viewport>,
  React.ComponentPropsWithoutRef<typeof ToastPrimitives.Viewport>
>(({ className, ...props }, ref) => (
  <ToastPrimitives.Viewport
    ref={ref}
    className={cn(
      "fixed top-0 z-[100] flex max-h-screen w-full flex-col-reverse p-4 sm:bottom-0 sm:right-0 sm:top-auto sm:flex-col md:max-w-[420px]",
      className
    )}
    {...props}
  />
))
ToastViewport.displayName = ToastPrimitives.Viewport.displayName

const toastVariants = cva(
  "group pointer-events-auto relative flex w-full items-center justify-between space-x-4 overflow-hidden rounded-md border p-6 pr-8 shadow-lg transition-all data-[swipe=cancel]:translate-x-0 data-[swipe=end]:translate-x-[var(--radix-toast-swipe-end-x)] data-[swipe=move]:translate-x-[var(--radix-toast-swipe-move-x)] data-[swipe=move]:transition-none data-[state=open]:animate-in data-[state=closed]:animate-out data-[swipe=end]:animate-out data-[state=closed]:fade-out-80 data-[state=closed]:slide-out-to-right-full data-[state=open]:slide-in-from-top-full data-[state=open]:sm:slide-in-from-bottom-full",
  {
    variants: {
      variant: {
        default: "border bg-background text-foreground",
        destructive:
          "destructive group border-destructive bg-destructive text-destructive-foreground",
      },
    },
    defaultVariants: {
      variant: "default",
    },
  }
)

const Toast = React.forwardRef<
  React.ElementRef<typeof ToastPrimitives.Root>,
  React.ComponentPropsWithoutRef<typeof ToastPrimitives.Root> &
    VariantProps<typeof toastVariants>
>(({ className, variant, ...props }, ref) => {
  return (
    <ToastPrimitives.Root
      ref={ref}
      className={cn(toastVariants({ variant }), className)}
      {...props}
    />
  )
})
Toast.displayName = ToastPrimitives.Root.displayName

const ToastAction = React.forwardRef<
  React.ElementRef<typeof ToastPrimitives.Action>,
  React.ComponentPropsWithoutRef<typeof ToastPrimitives.Action>
>(({ className, ...props }, ref) => (
  <ToastPrimitives.Action
    ref={ref}
    className={cn(
      " inline-flex h-8 shrink-0 items-center justify-center rounded-md border bg-transparent px-3 text-sm font-medium ring-offset-background transition-colors hover:bg-secondary focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2 disabled:pointer-events-none disabled:opacity-50 group-[.destructive]:border-muted/40 group-[.destructive]:hover:border-destructive/30 group-[.destructive]:hover:bg-destructive group-[.destructive]:hover:text-destructive-foreground group-[.destructive]:focus:ring-destructive",
      className
    )}
    {...props}
  />
))
ToastAction.displayName = ToastPrimitives.Action.displayName

const ToastClose = React.forwardRef<
  React.ElementRef<typeof ToastPrimitives.Close>,
  React.ComponentPropsWithoutRef<typeof ToastPrimitives.Close>
>(({ className, ...props }, ref) => (
  <ToastPrimitives.Close
    ref={ref}
    className={cn(
      "absolute right-2 top-2 rounded-md p-1 text-foreground/50 opacity-0 transition-opacity hover:text-foreground focus:opacity-100 focus:outline-none focus:ring-2 group-hover:opacity-100 group-[.destructive]:text-red-300 group-[.destructive]:hover:text-red-50 group-[.destructive]:focus:ring-red-400 group-[.destructive]:focus:ring-offset-red-600",
      className
    )}
    toast-close=""
    {...props}
  >
    <X className="h-4 w-4" />
  </ToastPrimitives.Close>
))
ToastClose.displayName = ToastPrimitives.Close.displayName

const ToastTitle = React.forwardRef<
  React.ElementRef<typeof ToastPrimitives.Title>,
  React.ComponentPropsWithoutRef<typeof ToastPrimitives.Title>
>(({ className, ...props }, ref) => (
  <ToastPrimitives.Title
    ref={ref}
    className={cn("text-sm font-semibold", className)}
    {...props}
  />
))
ToastTitle.displayName = ToastPrimitives.Title.displayName

const ToastDescription = React.forwardRef<
  React.ElementRef<typeof ToastPrimitives.Description>,
  React.ComponentPropsWithoutRef<typeof ToastPrimitives.Description>
>(({ className, ...props }, ref) => (
  <ToastPrimitives.Description
    ref={ref}
    className={cn("text-sm opacity-90", className)}
    {...props}
  />
))
ToastDescription.displayName = ToastPrimitives.Description.displayName

type ToastProps = React.ComponentPropsWithoutRef<typeof Toast>

type ToastActionElement = React.ReactElement<typeof ToastAction>

export {
  type ToastProps,
  type ToastActionElement,
  ToastProvider,
  ToastViewport,
  Toast,
  ToastTitle,
  ToastDescription,
  ToastClose,
  ToastAction,
}

"use client"

import { useToast } from "@/hooks/use-toast"
import {
  Toast,
  ToastClose,
  ToastDescription,
  ToastProvider,
  ToastTitle,
  ToastViewport,
} from "@/components/ui/toast"

export function Toaster() {
  const { toasts } = useToast()

  return (
    <ToastProvider>
      {toasts.map(function ({ id, title, description, action, ...props }) {
        return (
          <Toast key={id} {...props}>
            <div className="grid gap-1">
              {title && <ToastTitle>{title}</ToastTitle>}
              {description && (
                <ToastDescription>{description}</ToastDescription>
              )}
            </div>
            {action}
            <ToastClose />
          </Toast>
        )
      })}
      <ToastViewport />
    </ToastProvider>
  )
}

"use client"

// Inspired by react-hot-toast library
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

import type { ErrorSeverity } from "./types"

export interface ErrorMapping {
  userMessage: string
  severity: ErrorSeverity
  canRetry: boolean
  actionable?: string
  actionLabel?: string
  actionUrl?: string
}

export const DEFAULT_ERROR_MAPPINGS: Record<number, ErrorMapping> = {
  // 1xx Informational
  100: {
    userMessage: "Request is being processed...",
    severity: "info",
    canRetry: false,
  },

  // 2xx Success (shouldn't trigger error handler, but included for completeness)
  200: {
    userMessage: "Request completed successfully",
    severity: "info",
    canRetry: false,
  },

  // 3xx Redirection
  301: {
    userMessage: "The resource has moved permanently",
    severity: "info",
    canRetry: false,
  },
  302: {
    userMessage: "The resource has moved temporarily",
    severity: "info",
    canRetry: false,
  },

  // 4xx Client Errors
  400: {
    userMessage: "The information you provided is invalid. Please check and try again.",
    severity: "error",
    canRetry: false,
    actionable: "Review your input for any errors",
  },
  401: {
    userMessage: "You need to log in to access this resource.",
    severity: "warning",
    canRetry: false,
    actionable: "Please sign in to continue",
    actionLabel: "Sign In",
    actionUrl: "/login",
  },
  403: {
    userMessage: "You don't have permission to perform this action.",
    severity: "error",
    canRetry: false,
    actionable: "Contact support if you believe this is an error",
    actionLabel: "Contact Support",
    actionUrl: "/support",
  },
  404: {
    userMessage: "The requested resource could not be found.",
    severity: "error",
    canRetry: false,
    actionable: "Check the URL or navigate from the home page",
    actionLabel: "Go Home",
    actionUrl: "/",
  },
  405: {
    userMessage: "This action is not allowed for this resource.",
    severity: "error",
    canRetry: false,
  },
  408: {
    userMessage: "The request took too long to complete.",
    severity: "warning",
    canRetry: true,
    actionable: "Please try again",
    actionLabel: "Retry",
  },
  409: {
    userMessage: "There was a conflict with your request.",
    severity: "error",
    canRetry: false,
    actionable: "Please refresh the page and try again",
    actionLabel: "Refresh",
  },
  410: {
    userMessage: "This resource is no longer available.",
    severity: "error",
    canRetry: false,
    actionLabel: "Go Back",
  },
  422: {
    userMessage: "The data you provided could not be processed.",
    severity: "error",
    canRetry: false,
    actionable: "Please check your input and try again",
  },
  429: {
    userMessage: "You're making requests too quickly. Please slow down.",
    severity: "warning",
    canRetry: true,
    actionable: "Wait a moment before trying again",
    actionLabel: "Try Again",
  },

  // 5xx Server Errors
  500: {
    userMessage: "Something went wrong on our servers.",
    severity: "error",
    canRetry: true,
    actionable: "Please try again in a moment",
    actionLabel: "Retry",
  },
  501: {
    userMessage: "This feature is not yet available.",
    severity: "error",
    canRetry: false,
  },
  502: {
    userMessage: "Our service is temporarily unavailable.",
    severity: "error",
    canRetry: true,
    actionable: "Please try again",
    actionLabel: "Retry",
  },
  503: {
    userMessage: "Our service is temporarily down for maintenance.",
    severity: "error",
    canRetry: true,
    actionable: "Please try again in a few minutes",
    actionLabel: "Retry",
  },
  504: {
    userMessage: "The server took too long to respond.",
    severity: "warning",
    canRetry: true,
    actionable: "Please try again",
    actionLabel: "Retry",
  },
}

export const NETWORK_ERROR_MAPPINGS = {
  NETWORK_ERROR: {
    userMessage: "Unable to connect to the server. Please check your internet connection.",
    severity: "error" as ErrorSeverity,
    canRetry: true,
    actionable: "Verify your internet connection is stable",
    actionLabel: "Retry",
  },
  TIMEOUT_ERROR: {
    userMessage: "The request timed out. Please try again.",
    severity: "warning" as ErrorSeverity,
    canRetry: true,
    actionable: "Check your connection speed",
    actionLabel: "Retry",
  },
  PARSE_ERROR: {
    userMessage: "Unable to process the server response.",
    severity: "error" as ErrorSeverity,
    canRetry: true,
    actionLabel: "Retry",
  },
  UNKNOWN_ERROR: {
    userMessage: "An unexpected error occurred. Please try again.",
    severity: "error" as ErrorSeverity,
    canRetry: true,
    actionLabel: "Retry",
  },
}

export function getErrorMapping(statusCode: number, customMappings?: Record<number, string>): ErrorMapping {
  // Check custom mappings first
  if (customMappings?.[statusCode]) {
    return {
      userMessage: customMappings[statusCode],
      severity: statusCode >= 500 ? "error" : statusCode >= 400 ? "warning" : "info",
      canRetry: statusCode >= 500 || statusCode === 408 || statusCode === 429,
    }
  }

  // Use default mappings
  const mapping = DEFAULT_ERROR_MAPPINGS[statusCode]
  if (mapping) {
    return mapping
  }

  // Fallback based on status code range
  if (statusCode >= 500) {
    return {
      userMessage: "Server error occurred. Please try again later",
      severity: "error",
      canRetry: true,
    }
  } else if (statusCode >= 400) {
    return {
      userMessage: "Request failed. Please check your input and try again",
      severity: "error",
      canRetry: false,
    }
  } else {
    return NETWORK_ERROR_MAPPINGS.UNKNOWN_ERROR
  }
}

import type { ErrorContext, ErrorHandlerConfig, RequestConfig, RetryConfig } from "./types"
import { getErrorMapping, NETWORK_ERROR_MAPPINGS } from "./error-mapper"

class HttpInterceptor {
  private config: ErrorHandlerConfig
  private originalFetch: typeof fetch
  private retryConfig: RetryConfig
  private reportError: (error: ErrorContext) => void
  private reportedErrors = new Set<string>()
  private lastReportTime = 0
  private readonly REPORT_THROTTLE_MS = 1000

  constructor(config: ErrorHandlerConfig, reportError: (error: ErrorContext) => void) {
    this.config = config
    this.reportError = reportError
    this.originalFetch = globalThis.fetch
    this.retryConfig = {
      maxRetries: config.maxRetries,
      baseDelay: config.retryDelay,
      maxDelay: 30000, // 30 seconds max delay
      backoffFactor: 2,
    }
    this.setupInterceptor()
  }

  private setupInterceptor() {
    globalThis.fetch = this.interceptedFetch.bind(this)
  }

  public restore() {
    globalThis.fetch = this.originalFetch
  }

  public updateConfig(newConfig: ErrorHandlerConfig) {
    this.config = newConfig
    this.retryConfig.maxRetries = newConfig.maxRetries
    this.retryConfig.baseDelay = newConfig.retryDelay
  }

  private async interceptedFetch(input: RequestInfo | URL, init?: RequestConfig): Promise<Response> {
    const url = typeof input === "string" ? input : input instanceof URL ? input.href : input.url
    const method = init?.method || "GET"
    const skipErrorHandler = init?.skipErrorHandler || false
    const timeout = init?.timeout || this.config.timeoutMs
    const maxRetries = init?.retries ?? this.config.maxRetries

    // Remove custom properties from init to avoid fetch errors
    const { timeout: _, retries: __, skipErrorHandler: ___, ...fetchInit } = init || {}

    if (skipErrorHandler) {
      return this.originalFetch.call(globalThis, input, fetchInit)
    }

    return this.executeWithRetry(() => this.fetchWithTimeout(input, fetchInit, timeout), url, method, maxRetries)
  }

  private async fetchWithTimeout(input: RequestInfo | URL, init?: RequestInit, timeoutMs = 10000): Promise<Response> {
    const controller = new AbortController()
    const timeoutId = setTimeout(() => controller.abort(), timeoutMs)

    try {
      const response = await this.originalFetch.call(globalThis, input, {
        ...init,
        signal: controller.signal,
      })
      clearTimeout(timeoutId)
      return response
    } catch (error) {
      clearTimeout(timeoutId)
      if (error instanceof Error && error.name === "AbortError") {
        throw new Error("TIMEOUT_ERROR")
      }
      throw error
    }
  }

  private async executeWithRetry(
    fetchFn: () => Promise<Response>,
    url: string,
    method: string,
    maxRetries: number,
    retryCount = 0,
  ): Promise<Response> {
    try {
      const response = await fetchFn()

      // Check if response indicates an error
      if (!response.ok) {
        // Determine if we should retry
        const shouldRetry = this.shouldRetry(response.status, retryCount, maxRetries)

        if (shouldRetry) {
          await this.delay(this.calculateDelay(retryCount))
          return this.executeWithRetry(fetchFn, url, method, maxRetries, retryCount + 1)
        }

        // Only report error if we're not retrying
        const errorContext = this.createErrorContext(response.status, response.statusText, url, method, retryCount)
        this.reportErrorContext(errorContext)

        // Create a custom error that preserves the status code
        const error = new Error(`HTTP ${response.status}: ${response.statusText}`)
        ;(error as any).status = response.status
        ;(error as any).response = response
        throw error
      }

      return response
    } catch (error) {
      // Check if this is an HTTP error with a status code
      if ((error as any).status) {
        // This is an HTTP error, don't treat it as a network error
        throw error
      }

      const errorContext = this.createErrorContextFromException(error as Error, url, method, retryCount)

      // Determine if we should retry based on error type
      const shouldRetry = this.shouldRetryException(error as Error, retryCount, maxRetries)

      if (shouldRetry) {
        await this.delay(this.calculateDelay(retryCount))
        return this.executeWithRetry(fetchFn, url, method, maxRetries, retryCount + 1)
      }

      // Report error for non-retryable network errors
      this.reportErrorContext(errorContext)
      throw error
    }
  }

  private shouldRetry(statusCode: number, retryCount: number, maxRetries: number): boolean {
    if (!this.config.enableRetry || retryCount >= maxRetries) {
      return false
    }

    return this.config.retryableStatusCodes.includes(statusCode)
  }

  private shouldRetryException(error: Error, retryCount: number, maxRetries: number): boolean {
    if (!this.config.enableRetry || retryCount >= maxRetries) {
      return false
    }

    // Retry on network errors and timeouts
    return (
      error.message === "TIMEOUT_ERROR" ||
      error.message === "NETWORK_ERROR" ||
      error.name === "TypeError" || // Network errors often manifest as TypeError
      error.message.includes("fetch")
    )
  }

  private calculateDelay(retryCount: number): number {
    const delay = Math.min(
      this.retryConfig.baseDelay * Math.pow(this.retryConfig.backoffFactor, retryCount),
      this.retryConfig.maxDelay,
    )

    // Add jitter to prevent thundering herd
    return delay + Math.random() * 1000
  }

  private delay(ms: number): Promise<void> {
    return new Promise((resolve) => setTimeout(resolve, ms))
  }

  private createErrorContext(
    statusCode: number,
    message: string,
    url: string,
    method: string,
    retryCount: number,
  ): ErrorContext {
    const mapping = getErrorMapping(statusCode, this.config.customMessages)

    return {
      statusCode,
      message,
      timestamp: new Date(),
      url,
      method,
      userMessage: mapping.userMessage,
      canRetry: mapping.canRetry,
      retryCount,
      actionLabel: mapping.actionLabel,
      actionUrl: mapping.actionUrl,
    }
  }

  private createErrorContextFromException(error: Error, url: string, method: string, retryCount: number): ErrorContext {
    let mapping
    let statusCode = 0

    if (error.message === "TIMEOUT_ERROR") {
      mapping = NETWORK_ERROR_MAPPINGS.TIMEOUT_ERROR
      statusCode = 408
    } else if (error.name === "TypeError" || error.message.includes("fetch")) {
      mapping = NETWORK_ERROR_MAPPINGS.NETWORK_ERROR
      statusCode = 0
    } else {
      mapping = NETWORK_ERROR_MAPPINGS.UNKNOWN_ERROR
      statusCode = 0
    }

    const errorContext = {
      statusCode,
      message: error.message,
      timestamp: new Date(),
      url,
      method,
      userMessage: mapping.userMessage,
      canRetry: mapping.canRetry,
      retryCount,
      originalError: error,
    }

    // Report the error immediately
    this.reportErrorContext(errorContext)
    return errorContext
  }

  private reportErrorContext(errorContext: ErrorContext) {
    // Create a unique key for this error to prevent duplicates
    const errorKey = `${errorContext.statusCode}-${errorContext.url}-${errorContext.method}`
    const now = Date.now()

    // Throttle error reporting to prevent loops
    if (this.reportedErrors.has(errorKey) && now - this.lastReportTime < this.REPORT_THROTTLE_MS) {
      return
    }

    this.reportedErrors.add(errorKey)
    this.lastReportTime = now

    // Clear the error key after some time to allow future reports
    setTimeout(() => {
      this.reportedErrors.delete(errorKey)
    }, this.REPORT_THROTTLE_MS * 5)

    if (this.config.debugMode) {
      console.error("HTTP Error:", errorContext)
    }

    // Call the provided reportError callback
    this.reportError(errorContext)
  }
}

export { HttpInterceptor }

"use client";

import type React from "react";
import {
  createContext,
  useContext,
  useCallback,
  useState,
  useMemo,
  useEffect,
} from "react";
import type {
  ErrorHandlerConfig,
  ErrorHandlerContextType,
  ErrorContext,
} from "./types";
import { useToast } from "@/hooks/use-toast";
import { ToastAction } from "@/components/ui/toast";
import {
  AlertTriangle,
  XCircle,
  Wifi,
  Clock,
  Shield,
  Home,
  LogIn,
  RefreshCw,
  HelpCircle,
  Server,
} from "lucide-react";

const DEFAULT_CONFIG: ErrorHandlerConfig = {
  showToast: true,
  enableRetry: true,
  maxRetries: 3,
  retryDelay: 1000,
  debugMode: process.env.NODE_ENV === "development",
  customMessages: {},
  retryableStatusCodes: [408, 429, 500, 502, 503, 504],
  timeoutMs: 10000,
};

const ErrorHandlerContext = createContext<ErrorHandlerContextType | null>(null);

interface ErrorHandlerContextProviderProps {
  children: React.ReactNode;
  config?: Partial<ErrorHandlerConfig>;
}

export function ErrorHandlerContextProvider({
  children,
  config: initialConfig = {},
}: ErrorHandlerContextProviderProps) {
  const [config, setConfig] = useState<ErrorHandlerConfig>({
    ...DEFAULT_CONFIG,
    ...initialConfig,
  });

  const { toast } = useToast();

  const updateConfig = useCallback((newConfig: Partial<ErrorHandlerConfig>) => {
    setConfig((prev) => ({ ...prev, ...newConfig }));
  }, []);

  const reportError = useCallback(
    (errorContext: ErrorContext) => {
      if (!config.showToast) return;

      // Make 4xx and 5xx errors destructive to show severity
      const variant =
        errorContext.statusCode >= 400 ? "destructive" : "default";

      // Get appropriate icon for the error
      const ErrorIcon = getErrorIcon(errorContext.statusCode);

      // Create action element if needed
      let action = undefined;
      if (errorContext.actionLabel) {
        const ActionIcon = getActionIcon(
          errorContext.statusCode,
          errorContext.actionLabel
        );

        action = (
          <ToastAction
            altText={errorContext.actionLabel}
            onClick={() => {
              if (errorContext.actionUrl) {
                // Navigate to the URL
                if (typeof window !== "undefined") {
                  window.location.href = errorContext.actionUrl;
                }
              } else if (errorContext.onAction) {
                // Execute custom action
                errorContext.onAction();
              } else if (errorContext.canRetry) {
                // Default retry action - reload the page
                if (typeof window !== "undefined") {
                  window.location.reload();
                }
              }
            }}
          >
            <ActionIcon className="w-4 h-4" />
            <span className="pl-4">{errorContext.actionLabel}</span>
          </ToastAction>
        );
      }

      // Show toast notification with icon
      toast({
        variant,
        title: getErrorTitle(errorContext.statusCode),
        description: (
          <div className="flex items-center gap-2">
            <ErrorIcon className="w-4 h-4" />
            {errorContext.userMessage}
          </div>
        ),
        action,
        duration: getToastDuration(errorContext.statusCode),
      });

      // Log error in development
      if (config.debugMode) {
        console.group("ðŸš¨ HTTP Error Handler");
        console.error("Error Context:", errorContext);
        console.error("Toast Config:", {
          variant,
          title: getErrorTitle(errorContext.statusCode),
          description: errorContext.userMessage,
          hasAction: !!action,
        });
        console.groupEnd();
      }
    },
    [config.showToast, config.debugMode, toast]
  );

  // Listen for manual error reports
  useEffect(() => {
    const handleManualError = (event: CustomEvent<ErrorContext>) => {
      reportError(event.detail);
    };

    if (typeof window !== "undefined") {
      window.addEventListener(
        "manual-error-report",
        handleManualError as EventListener
      );
    }

    return () => {
      if (typeof window !== "undefined") {
        window.removeEventListener(
          "manual-error-report",
          handleManualError as EventListener
        );
      }
    };
  }, [reportError]);

  const contextValue: ErrorHandlerContextType = useMemo(
    () => ({
      config,
      updateConfig,
      reportError,
    }),
    [config, updateConfig, reportError]
  );

  return (
    <ErrorHandlerContext.Provider value={contextValue}>
      {children}
    </ErrorHandlerContext.Provider>
  );
}

export function useErrorHandler(): ErrorHandlerContextType {
  const context = useContext(ErrorHandlerContext);
  if (!context) {
    throw new Error(
      "useErrorHandler must be used within an ErrorHandlerProvider"
    );
  }
  return context;
}

function getErrorIcon(statusCode: number) {
  if (statusCode === 401) return LogIn;
  if (statusCode === 403) return Shield;
  if (statusCode === 404) return HelpCircle;
  if (statusCode === 408 || statusCode === 504) return Clock;
  if (statusCode === 429) return AlertTriangle;
  if (statusCode >= 500) return Server;
  if (statusCode === 0) return Wifi;
  return XCircle;
}

function getActionIcon(statusCode: number, actionLabel: string) {
  if (actionLabel.includes("Sign In") || actionLabel.includes("Login"))
    return LogIn;
  if (actionLabel.includes("Home")) return Home;
  if (actionLabel.includes("Retry") || actionLabel.includes("Try Again"))
    return RefreshCw;
  if (actionLabel.includes("Support")) return HelpCircle;
  return RefreshCw;
}

function getErrorTitle(statusCode: number): string {
  if (statusCode >= 500) return "Server Error";
  if (statusCode >= 400) return "Request Error";
  if (statusCode === 0) return "Network Error";
  return "Error";
}

function getToastDuration(statusCode: number): number {
  // Critical errors stay longer
  if (statusCode >= 500 || statusCode === 0) return 8000;
  // Client errors stay medium time
  if (statusCode >= 400) return 6000;
  // Other errors stay shorter
  return 4000;
}

"use client"

import React, { useEffect, useRef } from "react"
import { ErrorHandlerContextProvider, useErrorHandler } from "./context"
import { HttpInterceptor } from "./interceptor"
import type { ErrorHandlerConfig } from "./types"

interface ErrorHandlerProviderProps {
  children: React.ReactNode
  config?: Partial<ErrorHandlerConfig>
}

export function ErrorHandlerProvider({ children, config = {} }: ErrorHandlerProviderProps) {
  return (
    <ErrorHandlerContextProvider config={config}>
      <ErrorBoundary>
        <InterceptorManager />
        {children}
      </ErrorBoundary>
    </ErrorHandlerContextProvider>
  )
}

// Separate component to manage interceptor lifecycle
function InterceptorManager() {
  const interceptorRef = useRef<HttpInterceptor | null>(null)
  const { config, reportError } = useErrorHandler()
  const configRef = useRef(config)
  const reportErrorRef = useRef(reportError)

  // Update refs when values change
  useEffect(() => {
    configRef.current = config
    reportErrorRef.current = reportError
  }, [config, reportError])

  useEffect(() => {
    // Initialize interceptor only once
    if (!interceptorRef.current) {
      interceptorRef.current = new HttpInterceptor(configRef.current, reportErrorRef.current)
    }

    return () => {
      // Cleanup interceptor on unmount
      if (interceptorRef.current) {
        interceptorRef.current.restore()
        interceptorRef.current = null
      }
    }
  }, []) // Empty dependency array - only run once

  // Update interceptor config when it changes
  useEffect(() => {
    if (interceptorRef.current) {
      interceptorRef.current.updateConfig(configRef.current)
    }
  }, [config])

  return null
}

// Error Boundary Component
class ErrorBoundary extends React.Component<{ children: React.ReactNode }, { hasError: boolean; error?: Error }> {
  constructor(props: { children: React.ReactNode }) {
    super(props)
    this.state = { hasError: false }
  }

  static getDerivedStateFromError(error: Error) {
    return { hasError: true, error }
  }

  componentDidCatch(error: Error, errorInfo: React.ErrorInfo) {
    console.error("Error Boundary caught an error:", error, errorInfo)
  }

  render() {
    if (this.state.hasError) {
      return (
        <div className="min-h-screen flex items-center justify-center bg-background">
          <div className="text-center p-8">
            <h2 className="text-2xl font-bold text-foreground mb-4">Something went wrong</h2>
            <p className="text-muted-foreground mb-6">
              We apologize for the inconvenience. Please refresh the page to continue.
            </p>
            <button
              onClick={() => window.location.reload()}
              className="px-4 py-2 bg-primary text-primary-foreground rounded-md hover:bg-primary/90 transition-colors"
            >
              Refresh Page
            </button>
          </div>
        </div>
      )
    }

    return this.props.children
  }
}

export { ErrorHandlerProvider } from "./provider"
